const { DataTypes } = require( 'sequelize' );

module.exports = {

    id: {
        allowNull: false,
        primaryKey: true,
        type: DataTypes.UUID,
        defaultValue: DataTypes.UUIDV4,
    },

    order_id: {
        allowNull: false,
        type: DataTypes.UUID,
        references: {
            model: 'orders',
            key: 'id',
        },
    },

    response_code: {
        allowNull: false,
        type: DataTypes.STRING,
    },

    gateway_transaction_id: {
        allowNull: false,
        type: DataTypes.STRING,
    },

    gateway_transaction_time: {
        allowNull: false,
        type: DataTypes.DATE,
    },

    created_at: {
        allowNull: false,
        type: DataTypes.DATE,
        defaultValue: DataTypes.NOW,
    },

    updated_at: {
        allowNull: false,
        type: DataTypes.DATE,
        defaultValue: DataTypes.NOW,
    },

};
