const { DataTypes } = require( 'sequelize' );

module.exports = {

    id: {
        allowNull: false,
        primaryKey: true,
        type: DataTypes.UUID,
        defaultValue: DataTypes.UUIDV4,
    },

    online_merchant_id: {
        allowNull: false,
        type: DataTypes.UUID,
        references: {
            model: 'online_merchants',
            key: 'id',
            onDelete: 'CASCADE',
        }
    },

    tag_id: {
        allowNull: false,
        type: DataTypes.UUID,
        references: {
            model: 'tags',
            key: 'id',
            onDelete: 'CASCADE',
        }
    },

    created_at: {
        allowNull: false,
        type: DataTypes.DATE,
        defaultValue: DataTypes.NOW,
    },

    updated_at: {
        allowNull: false,
        type: DataTypes.DATE,
        defaultValue: DataTypes.NOW,
    },
};
