const { DataTypes } = require( 'sequelize' );

module.exports = {

    id: {
        allowNull: false,
        primaryKey: true,
        type: DataTypes.UUID,
        defaultValue: DataTypes.UUIDV4,
    },

    name: {
        allowNull: false,
        type: DataTypes.STRING
    },

    commission_percentage: {
        allowNull: false,
        type: DataTypes.FLOAT
    },

    transaction_type_id: {
        allowNull: false,
        type: DataTypes.INTEGER
    },

    created_at: {
        allowNull: false,
        type: DataTypes.DATE,
        defaultValue: DataTypes.NOW,
    },

    updated_at: {
        allowNull: false,
        type: DataTypes.DATE,
        defaultValue: DataTypes.NOW,
    }

};
