const mongoose = require( 'mongoose' );
const config = require( '../config' );

exports.assertConnection = function assertConnection() {

    const { host, port, ...mongoConfig } = config.get( '/mongo' );
    const connectionString = `mongodb://${ host }:${ port }`;

    return mongoose.connect( connectionString, mongoConfig );

};

exports.Ruleset = require( './ruleset' );
