const mongoose = require( 'mongoose' );

const schema = new mongoose.Schema( {

    cardTypeId: {
        type: String,
        required: true,
    },

    conditions: {
        _id: false,
        all: [
            {
                _id: false,
                fact: {
                    type: String,
                    _id: false,
                },
                operator: {
                    type: String,
                    _id: false,
                },
                value: {
                    type: mongoose.Schema.Types.Mixed,
                    _id: false,
                }
            }
        ]

    },

    event: {

        _id: false,
        type: {
            type: String,
        },
        params: {
            _id: false,
            type: Object
        }

    }

} );

module.exports = mongoose.model( 'Ruleset', schema );
