const { Sequelize: { Model } } = require( 'sequelize' );
const sequelizePaginate = require( 'sequelize-paginate' );
const { CardAdditionRequest: CardAdditionRequestSchema } = require( '../schema' );

class CardAdditionRequest extends Model {}

module.exports = sequelize => {

    CardAdditionRequest.init( CardAdditionRequestSchema, { sequelize, tableName: 'card_addition_requests', underscored: true, timestamps: true } );
    CardAdditionRequest.associate = models => {

        CardAdditionRequest.belongsTo( models.Session, { foreignKey: 'sessionId' } );
        CardAdditionRequest.belongsTo( models.VaultRequestMetadata, { foreignKey: 'vaultRequestMetadataId' } );

    };

    sequelizePaginate.paginate( CardAdditionRequest );
    return CardAdditionRequest;

};
