const { Sequelize: { Model } } = require( 'sequelize' );
const { CardCategory: CardCategorySchema } = require( '../schema' );

class CardCategory extends Model {}

module.exports = sequelize => {

    CardCategory.init( CardCategorySchema, { sequelize, tableName: 'card_categories', underscored: true, timestamps: true } );
    CardCategory.associate = models => {

        CardCategory.belongsTo( models.Bank, { foreignKey: 'bankId' } );

    };

    return CardCategory;

};
