const { Sequelize: { Model } } = require( 'sequelize' );
const { CardType: CardTypeSchema } = require( '../schema' );

class CardType extends Model {}

module.exports = sequelize => {

    CardType.init( CardTypeSchema, { sequelize, tableName: 'card_types', underscored: true, timestamps: true } );
    CardType.associate = models => {

        CardType.belongsTo( models.CardCategory, { foreignKey: 'categoryId' } );

    };

    return CardType;

};
