const { Sequelize: { Model } } = require( 'sequelize' );
const sequelizePaginate = require( 'sequelize-paginate' );
const { CuratedGiftcard: CuratedGiftcardSchema } = require( '../schema' );

class CuratedGiftcard extends Model {}

module.exports = sequelize => {

    CuratedGiftcard.init( CuratedGiftcardSchema, { sequelize, tableName: 'curated_giftcards', underscored: true, timestamps: true } );
    CuratedGiftcard.associate = models => {

        CuratedGiftcard.belongsTo( models.Giftcard, { foreignKey: 'giftcardId' } );

    };

    sequelizePaginate.paginate( CuratedGiftcard );
    return CuratedGiftcard;

};
