const { Sequelize: { Model } } = require( 'sequelize' );
const sequelizePaginate = require( 'sequelize-paginate' );
const { CuratedOffer: CuratedOfferSchema } = require( '../schema' );

class CuratedOffer extends Model {}

module.exports = sequelize => {

    CuratedOffer.init( CuratedOfferSchema, { sequelize, tableName: 'curated_offers', underscored: true, timestamps: true } );

    sequelizePaginate.paginate( CuratedOffer );
    return CuratedOffer;

};
