const { Sequelize: { Model } } = require( 'sequelize' );
const { GyftrSlab: GyftrSlabSchema } = require( '../schema' );

class GyftrSlab extends Model {}

module.exports = sequelize => {

    GyftrSlab.init( GyftrSlabSchema, { sequelize, tableName: 'gyftr_slabs', underscored: true, timestamps: true } );
    return GyftrSlab;

};
