const { Sequelize: { Model } } = require( 'sequelize' );
const { OnlineOfferFinance: OnlineOfferFinanceSchema } = require( '../schema' );

class OnlineOfferFinance extends Model {}

module.exports = sequelize => {

    OnlineOfferFinance.init( OnlineOfferFinanceSchema, { sequelize, tableName: 'online_offer_finances', underscored: true, timestamps: true } );
    OnlineOfferFinance.associate = models => {

        OnlineOfferFinance.belongsTo( models.Finance, { foreignKey: 'financeId' } );
        OnlineOfferFinance.belongsTo( models.OnlineOffer, { foreignKey: 'offerId' } );

    };

    return OnlineOfferFinance;

};
