const { Sequelize: { Model } } = require( 'sequelize' );
const { PaymentMethod: PaymentMethodSchema } = require( '../schema' );

class PaymentMethod extends Model {}

module.exports = sequelize => {

    PaymentMethod.init( PaymentMethodSchema, { sequelize, tableName: 'payment_methods', underscored: true, timestamps: true } );
    return PaymentMethod;

};
