const { Sequelize: { Model } } = require( 'sequelize' );
const { QcRange: QcRangeSchema } = require( '../schema' );

class QcRange extends Model {}

module.exports = sequelize => {

    QcRange.init( QcRangeSchema, { sequelize, tableName: 'qc_ranges', underscored: true, timestamps: true } );
    return QcRange;

};
