const { Sequelize: { Model } } = require( 'sequelize' );
const { QcSlab: QcSlabSchema } = require( '../schema' );

class QcSlab extends Model {}

module.exports = sequelize => {

    QcSlab.init( QcSlabSchema, { sequelize, tableName: 'qc_slabs', underscored: true, timestamps: true } );
    return QcSlab;

};
