const { Sequelize: { Model } } = require( 'sequelize' );
const { RevokedToken: RevokedTokenSchema } = require( '../schema' );

class RevokedToken extends Model {}

module.exports = sequelize => {

    RevokedToken.init( RevokedTokenSchema, { sequelize, tableName: 'revoked_tokens', underscored: true, timestamps: true } );

    return RevokedToken;

};
