const { Sequelize: { Model } } = require( 'sequelize' );
const { Tag: TagSchema } = require( '../schema' );

class Tag extends Model {}

module.exports = sequelize => {

    Tag.init( TagSchema, { sequelize, tableName: 'tags', underscored: true, timestamps: true } );
    Tag.associate = models => {

        Tag.belongsToMany( models.OnlineMerchant, { through: 'online_merchant_tags' } );

    };

    return Tag;

};
