const { Sequelize: { Model } } = require( 'sequelize' );
const { UserGiftcard: UserGiftcardSchema } = require( '../schema' );

class UserGiftcard extends Model {}

module.exports = sequelize => {

    UserGiftcard.init( UserGiftcardSchema, { sequelize, tableName: 'user_giftcards', underscored: true, timestamps: true } );
    return UserGiftcard;

};
