const { Sequelize: { Model } } = require( 'sequelize' );
const { UserSaving: UserSavingSchema } = require( '../schema' );

class UserSaving extends Model {}

module.exports = sequelize => {

    UserSaving.init( UserSavingSchema, { sequelize, tableName: 'user_savings', underscored: true, timestamps: true } );
    UserSaving.associate = models => {

        UserSaving.belongsTo( models.User, { foreignKey: 'userId' } );

    };

    return UserSaving;

};
