const { DataTypes } = require( 'sequelize' );

module.exports = {

    id: {
        allowNull: false,
        primaryKey: true,
        type: DataTypes.UUID,
        defaultValue: DataTypes.UUIDV4,
    },

    name: {
        allowNull: false,
        type: DataTypes.STRING
    },

    commissionPercentage: {
        allowNull: false,
        type: DataTypes.FLOAT
    },

    transactionTypeId: {
        allowNull: false,
        type: DataTypes.INTEGER
    },

    createdAt: {
        allowNull: false,
        type: DataTypes.DATE,
        defaultValue: DataTypes.NOW,
    },

    updatedAt: {
        allowNull: false,
        type: DataTypes.DATE,
        defaultValue: DataTypes.NOW,
    }

};
