const { DataTypes } = require( 'sequelize' );

module.exports = {

    id: {
        type: DataTypes.UUID,
        allowNull: false,
        primaryKey: true,
        defaultValue: DataTypes.UUIDV4,
    },

    userId: {
        type: DataTypes.UUID,
        allowNull: false,
        references: {
            model: 'users',
            key: 'id',
            onDelete: 'CASCADE',
        },
    },

    savingAmount: {
        type: DataTypes.FLOAT,
        allowNull: false,
    },

    savingType: {
        type: DataTypes.ENUM( 'GIFTCARD', 'COUPON' ), // eslint-disable-line new-cap
        allowNull: false,
    },

    isConfirmedSaving: {
        type: DataTypes.BOOLEAN,
        allowNull: false,
        defaultValue: true,
    },

    createdAt: {
        allowNull: false,
        type: DataTypes.DATE,
        defaultValue: DataTypes.NOW,
    },

    updatedAt: {
        allowNull: false,
        type: DataTypes.DATE,
        defaultValue: DataTypes.NOW,
    },

};
